mapper.insert:
INSERT INTO API_AUTHKEY
    (api_flag, keyvalue, regdate)
    VALUES('ELAND', #{token}, FORMAT(GETDATE(), 'yyyyMMddHHmmss'));

mapper.select:
SELECT C.con_name AS strAcmName , C.con_flag AS strFlag , C.strChainCode , C.con_addr_new , (SELECT stend_pri FROM goods WITH(NOLOCK) WHERE con_id = C.con_id AND CONVERT(CHAR(10), good_date, 120) = #{strEnterIn}
AND REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(tocode, '<', ''), '>', ''), ']', ''), '[', ''), '&', ''), 'â˜…', ''), '/', ''), ' ', ''), '(', ''), ')', ''), '+', '') = #{strRoomTypeName}
) AS moneyCost FROM condo C WITH(NOLOCK) WHERE C.con_id = ${con_id}

SELECT tocode
FROM goods WITH(NOLOCK)
WHERE con_id = ${con_id}
  AND CONVERT(CHAR(10), good_date, 120) = #{strEnterIn}
  AND REPLACE(tocode,' ','+') = Replace(#{strRoomTypeName},' ','+')
